import React, { useState, useEffect, useNavigate } from "react";
import ListService from "../services/list.service";
import "./style.css";

const AllListComponent = () => {
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <AllList/>
        </div>
      </div>
    </main>
  );
};


const AllList = () => {
  const [lists, setLists] = useState([]);
  const navigate = useNavigate();

  const addList = () => {
    // console.log("add list");
    // const name = `New List ${lists.length+1}`;
    // ListService.addList(name, '')
    // .then((id) => {
    //   console.log(id);
    //   navigate(`/mylist/${id}`);
    // })
    // .catch((err) => {
    //   console.log(err);
    // });
  }

  const deleteList = (id) => {
    setLists(lists.filter(list => list.id !== id));
    ListService.deleteList(id);
  }

  useEffect(() => {
    ListService.getList()
    .then(({data}) => {
      setLists(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = lists.map((list, idx) => 
    <List key={idx} list={list} deleteList={deleteList}/>
  );

  return(
    <div className="row">
      <h3>Shopping Lists</h3>
      <button className="btn btn-round" onClick={addList}>
        <i className="bi bi-plus-lg"></i>
      </button> 
      <ul>
        {row}
      </ul>
    </div>
  )
}

const List = ({list, deleteList}) => {
  const navigate = useNavigate();
  return(
    <li>
      <button className="btn btn-round" onClick={() => deleteList(list.id)}>
        <i className="bi bi-x-lg"></i>
      </button>
      <p> {list.name} </p>
      <button className="btn btn-round" onClick={() => navigate(`/mylist/${list.id}`)}>
        <i className="bi bi-pencil-fill"></i>
      </button>
    </li>
  )
}

export default AllListComponent;
