import { useParams, useNavigate } from "react-router-dom";
import RecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipePage = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const {recipeId} = useParams();

  useEffect(() => {
    RecipeService.getMyRecipe(recipeId)
    .then(({data}) => {
      setRecipe(data[0])
    }).catch((err) => {
      console.log(err)
    });

