import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import UserService from "../services/user.service";
import FridgeService from "../services/fridge.service";
import "./style.css";

const HomeComponent = () => {
  const [fridges, setFridges] = useState([]);

  const deleteFridge = () => {
    console.log("hi");
  }

  useEffect(() => {
    FridgeService.getFridge()
    .then(({data}) => {
      setFridges(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <h1 className="display-5 fw-bold">BOBA</h1>
          <div className="row">
          <h3>Fridge</h3>
          <i className="bi bi-apple"></i>
          <table className="table table-borderless">
            <thead>
              <tr>
                <th></th>
                <th scope="col">ingredient</th>
                <th scope="col">qty</th>
                <th scope="col">date</th>
                <th scope="col">exp date</th>
              </tr>
            </thead>
            <tbody>
              {fridges && (fridges.map((ingr) => (
                <tr key={ingr._id}>
                  <td> # </td>
                  <td> {ingr.ingr} </td>
                  <td > {ingr.qty} </td>
                  <td> {ingr.date.substring(0,10)} </td>
                  <td> {ingr.exp_date.substring(0,10)} </td>
                  <td className="td-icon">
                    <button className="btn btn-round" onClick={() => {deleteFridge()}}>
                      <i className="bi bi-search"></i>
                    </button>
                    <button className="btn btn-round" onClick={() => {  }}>
                      <i className="bi bi-pencil-fill"></i>
                    </button>
                  </td>
                </tr>
              )))}
            </tbody>
          </table>
          </div>
        </div>

        <div className="row">
          <h3> Shopping Lists</h3>

        </div>
        <footer className="pt-3 mt-4 text-muted border-top">
          &copy; 2024 Cheng-Yun Cheng
        </footer>
      </div>
    </main>
  );
};

export default HomeComponent;
