import React, { useState, useEffect } from "react";
import FridgeService from "../services/fridge.service";
import ListService from "../services/list.service";
import "./style.css";

const HomeComponent = () => {
  const [fridges, setFridges] = useState([]);

  const addFridge = () => {
    console.log(`addFridge`);
  }
  const deleteFridge = (id) => {
    console.log(`deleteFridge ${id}`);
  }

  useEffect(() => {
    FridgeService.getFridge()
    .then(({data}) => {
      setFridges(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <h1 className="display-5 fw-bold">BOBA</h1>
          <div className="row">
          <h3>Fridge</h3>
          <table className="table table-borderless">
            <thead>
              <tr>
                <th></th>
                <th scope="col">ingredient</th>
                <th scope="col">qty</th>
                <th scope="col">date</th>
                <th scope="col">exp date</th>
              </tr>
            </thead>
            <tbody>
              {fridges && (fridges.map((ingr) => (
                <tr key={ingr.id}>
                  <td> 
                    <button className="btn btn-round" onClick={() => {deleteFridge(ingr.id)}}>
                    <i className="bi bi-x-lg"></i>
                    </button> 
                  </td>
                  <td> {ingr.ingr} </td>
                  <td > <QtySelect id={ingr.id} qty={ingr.qty}/> </td>
                  <td> {ingr.date.substring(0,10)} </td>
                  <td> {ingr.exp_date.substring(0,10)} </td>
                </tr>
              )))}
              <tr>
                <td> 
                  <button className="btn btn-round" onClick={() => {addFridge()}}>
                    <i className="bi bi-plus-lg"></i>
                  </button> 
                </td> 
              </tr>
            </tbody>
          </table>
          </div>
        </div>

        <div className="row">
          <h3> Shopping Lists</h3>

        </div>
        <footer className="pt-3 mt-4 text-muted border-top">
          &copy; 2024 Cheng-Yun Cheng
        </footer>
      </div>
    </main>
  );
};

const QtySelect = (props) => {
  const [qty, setQty] = useState(props.qty);
  const id = props.id;

  const options = [];
  for(var i = 1; i <= 10; i++) {
    options.push({'value': i, label: i});
  }

  const handleChange = (event) => {
    const new_qty = parseInt(event.target.value);
    setQty(new_qty);
    FridgeService.updateFridge(id, new_qty);
  }
  return (
    <>
      <select value={qty} onChange={handleChange}>
        {options.map(({value, label}, idx) => (<option key={idx} value={value}> {label} </option>))}
      </select>
    </>
  )
};



export default HomeComponent;
