import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import RecipeService from "../../services/recipe.service";
import { Col, Container, Row, Image } from "react-bootstrap";
import "../style.css";

const RecipePage = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState({});
  const [ingrs, setIngrs] = useState([]);
  const {recipeId} = useParams();

  useEffect(() => {
    RecipeService.getRecipeItem(recipeId)
    .then(({data}) => {
      setIngrs(data);
    }).catch((err) => {
      console.log(err)
    });

    RecipeService.getRecipe(recipeId)
    .then(({data}) => {
      setRecipe(data[0])
    }).catch((err) => {
      console.log(err)
    });
  }, []);

  return (
    <main>
      <Container className="py-5">
        <Title recipe={recipe}/>
        <RecipeImage />
        <Description desc={recipe.desc}/>
        <Ingredients ingrs={ingrs}/>
        <Steps step={recipe.step}/>
      </Container>
    </main>
  );
};

const Title = ({recipe}) => {
  return(
    <Container>
      <Row>
        <Col as="h2"> {recipe.name} </Col>
        <Col style={{"textAlign": "right"}}>
          <button className="btn btn-round" onClick={() => {}}>
              <i className="bi bi-pencil-fill"></i>
          </button>
        </Col>
        <hr/>
        <Col as="p" style={{"textAlign": "right"}}>
          {recipe.user}, {recipe.post_time}
        </Col>
      </Row>
    </Container>
  );
};

const RecipeImage = () => {
  return(
    <Container>
      <Image src="boba.jpg"/>
    </Container>
  )
}

const Description = ({desc}) => {
  return(
    <Container className="px-5">
      <Row as="h4"> Description: </Row>
      <Row> {desc} </Row>
    </Container>
  );
};

const Ingredients = ({ingrs}) => {
  return(
    <Container className="py-4 px-2">
      <Row as="h4"> Ingredients: </Row>
      <Row as ="p"> {ingrs.map((ingr) => <>{ingr.name}</>)} </Row>
    </Container>
  );
};

const Steps = ({step}) => {
  return(
    <Container className="py-4">
      <Row as="h4"> Steps: </Row>
      <Row as="p"> {step} </Row>
    </Container>
  );
};

export default RecipePage;
