import axios from "axios";
const API_URL = "http://localhost:8080/api/fridge";

class FridgeService {

  static getFridge(){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.get(API_URL, {headers: {Authorization: token}});
  }

  static addFridge(){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.post(API_URL, {headers: {Authorization: token}});
  }

  static updateFridge(id, qty){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.put(API_URL, {id, qty, headers: {Authorization: token}});
  }

  static deleteFridge(){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.delete(API_URL, {headers: {Authorization: token}});
  }
}

export default FridgeService;
