import React, { useState, useEffect } from "react";
import FridgeService from "../services/fridge.service";
import ListService from "../services/list.service";
import "./style.css";

const HomeComponent = () => {
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <Fridge/>
        </div>
      </div>
    </main>
  );
};



const Fridge = () => {
  return(
    <div className="row">
       <h3>Fridge</h3>
       <FridgeTable/>
     </div>
  )
}

const FridgeTable = () => {
  const [fridge, setFridge] = useState([]);

  // const addFridge = () => {
  //   console.log(`addFridge`);
  // }

  const updateQty = (id, new_qty) => {
    const new_fridge = fridge.map(item => {
      if(item.id == id){
        item.qty = new_qty;
      } 
      return item;
    })
  }

  const deleteFridge = (id) => {
    setFridge(fridge.filter(a => a.id !== id))
    console.log(`deleteFridge ${id}`);
  }

  useEffect(() => {
    FridgeService.getFridge()
    .then(({data}) => {
      setFridge(data);
      console.log(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = fridge.map((item, idx) => 
    <FridgeItem key={idx} item={item} deleteFridge={deleteFridge}/>
  );

  return(
    <table className="table table-borderless">
      <thead>
        <tr>
          <th></th>
          <th scope="col">ingredient</th>
          <th scope="col">qty</th>
          <th scope="col">date</th>
          <th scope="col">exp date</th>
        </tr>
      </thead>
      <tbody>
        {row}
        <tr>
          <td> 
            <button className="btn btn-round" onClick={() => {}}>
              <i className="bi bi-plus-lg"></i>
            </button> 
          </td> 
        </tr>
      </tbody>
    </table>
  )
}

const FridgeItem = ({item, deleteFridge}) => {
  return(
    <tr>
      <td> 
        <button className="btn btn-round" onClick={()=>{deleteFridge(item.id)}}>
          <i className="bi bi-x-lg"></i>
        </button> 
      </td>
      <td> {item.ingr} </td>
      <td > <QtySelect qty={item.qty}/> </td>
      <td> {item.date.substring(0,10)} </td>
      <td> {item.exp_date.substring(0,10)} </td>
    </tr>
  )
}

const QtySelect = ({qty}) => {
  const options = [];
  for(var i = 1; i <= 10; i++) {
    options.push({'value': i, label: i});
  }
  return (
    <select value={qty} onChange={()=>{}}>
      {options.map(({value, label}, idx) => (<option key={idx} value={value}> {label} </option>))}
    </select>
  )
};

const AddItem = () => {
  return(
    <tr>

    </tr>
  )
