import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import MyRecipeService from "../../services/myrecipe.service";
import { Col, Container, Row, Image } from "react-bootstrap";
import "../style.css";

const MyRecipePage = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [ingrs, setIngrs] = useState([]);
  const {recipeId} = useParams();

  useEffect(() => {
    MyRecipeService.getRecipeItem(recipeId)
    .then(({data}) => {
      setIngrs(data);
    }).catch((err) => {
      console.log(err)
    });

    MyRecipeService.getMyRecipe(recipeId)
    .then(({data}) => {
      setRecipe(data[0])
    }).catch((err) => {
      console.log(err)
    });
  }, [recipeId]);

  if(recipe){
    return (
      <main>
        <div className="container py-4">
          <div className="container-fluid py-5">
            <h2> 
              {recipe.name}
              <button className="btn btn-round" onClick={() => navigate(`/myrecipe/${recipe.id}/edit`)}>
                <i className="bi bi-pencil-fill"></i>
              </button> 
            </h2>
            <p> {recipe.desc} </p>
            <p>
              {ingrs.map((ingr, idx) => ingr.name)}
            </p>
            <p> {recipe.steps} </p>
          </div>
        </div>
      </main>
    );
  }
  else{
    return(
      <></>
    )
  }
};

const Title = ({recipe}) => {
  const [like, setLike] = useState(false);
  const [nliked, setNliked] = useState(0)

  const handleLiked = () => {
    if(like){
      RecipeService.deleteLiked(recipe.id);
      setNliked(nliked-1);
    }
    else{
      RecipeService.addLiked(recipe.id);
      setNliked(nliked+1);
    }
    setLike(!like);
  }

  useEffect(() => {
    if(recipe.nliked)
      setNliked(recipe.nliked)
    if(recipe.id)
      RecipeService.getLiked(recipe.id)
      .then(({data}) => {
        data.length === 0 ? setLike(false) : setLike(true);
      }).catch((err) => {
        console.log(err)
      });
  }, [recipe]);
  return(
    <Container>
      <Row>
        <Col as="h2"> {recipe.name.toUpperCase()} </Col>
        <Col className="text-end">
          <button className="btn btn-round" onClick={handleLiked}>
            {!like && (<i className="bi bi-heart"></i>)}
            {like && (<i className="bi bi-heart-fill text-danger"></i>)}
          </button>
        </Col>
        <hr/>
      </Row>
      <Row>
        <Col as="h5"> Liked: {nliked}</Col>
        <Col className="text-end" as="p">
          <a href={`/recipes/user/${recipe.user}`}>{recipe.user_name}</a>
          , {recipe.post_time.substring(0,10)}
        </Col>
      </Row>
    </Container>
  );
};

const RecipeImage = () => {
  return(
    <Container className="py-2" style={{"textAlign": "center"}}>
      <Image src="../boba.jpg"/>
    </Container>
  )
}

const Description = ({desc}) => {
  return(
    <Container className="py-2 px-5" style={{"fontSize": "15pt"}}>
      <Row as="h3"> Description: </Row>
      <Row className="px-3"> {desc} </Row>
    </Container>
  );
};

const Ingredients = ({ingrs}) => {
  const ingrsName = ingrs.map((ingr) => ingr.name);
  return(
    <Container className="py-2 px-5" style={{"fontSize": "15pt"}}>
      <Row as="h3"> Ingredients: </Row>
      <Row className="px-3"> {ingrsName.join(', ')} </Row>
    </Container>
  );
};

const Steps = ({steps}) => {
  return(
    <Container className="py-2 px-5" style={{"fontSize": "15pt"}}>
      <Row as="h3"> Steps: </Row>
      <Row className="px-3"> 
        <ol>
          {steps.split('\n').map((step, idx) =>
            <li key={idx}> {step} </li>
          )}
        </ol>
      </Row>

    </Container>
  );
};

export default MyRecipePage;
