import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Card } from "react-bootstrap";
import MyRecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipeHome = () => {
  return (
    <main>
      <Container fluid>
          <MyRecipe/>
          <LikedRecipe/>
      </Container>
    </main>
  );
};

const Recipe = ({recipe}) => {
  const navigate = useNavigate()
  return(
    <div>
      <h5> 
        <a href={`/myrecipe/${recipe.id}`}>{recipe.name}</a>
        <button className="btn btn-round" onClick={() => navigate(`/myrecipe/${recipe.id}/edit`)}>
          <i className="bi bi-pencil-fill"></i>
        </button> 
      </h5>
    </div>
  );
};

const MyRecipe = () => {
  const [recipes, setRecipes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    MyRecipeService.getAllRecipe()
    .then(({data}) => {
      setRecipes(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return(
    <Container className="py-5" fluid>
      <h3>MY RECIPES
        <button className="btn btn-round" onClick={() => navigate("/myrecipe/newrecipe")}>
          <i className="bi bi-plus-lg"></i>
        </button> 
      </h3>
      <Row className="g-4">
        {Array.from({ length: 10 }).map((_, idx) => (
          <Col key={idx}>
            <Card>
              <Card.Img variant="top" src="holder.js/100px160" />
              <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                  This is a longer card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {recipes.map((recipe, idx) => 
        <Recipe key={idx} recipe={recipe}/>
      )}
    </Container>
  );
};

const LikedRecipe = () => {
  return(
    <div>
      <h3> Liked Recipes </h3>
    </div>
  );
};

export default MyRecipeHome;
