import React, { useState, useEffect } from "react";
import ListService from "../services/list.service";
import IngrService from "../services/ingr.service";
import "./style.css";

const MyListComponent = () => {
  const {id} = useParams();
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <MyList/>
        </div>
      </div>
    </main>
  );
};



const MyList = () => {
  return(
    <>
      <ListForm/>
      <ListTable/>
    </>
  )
}

const ListForm = () => {
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
}

const ListTable = () => {
  const [fridge, setFridge] = useState([]);

  const addItem = (item) => {
    const new_fridge = [...fridge, item];
    setFridge(new_fridge);
    FridgeService.addFridge(item);
  }

  const updateQty = (id, new_qty) => {
    const new_fridge = fridge.map(item => {
      if(item.id == id){
        item.qty = new_qty;
      } 
      return item;
    })
    setFridge(new_fridge);
    FridgeService.updateFridge(id, new_qty);
  }

  const deleteItem = (id) => {
    setFridge(fridge.filter(item => item.id !== id));
    FridgeService.deleteFridge(id);
  }

  useEffect(() => {
    FridgeService.getFridge()
    .then(({data}) => {
      setFridge(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = fridge.map((item, idx) => 
    <FridgeItem key={idx} item={item} deleteItem={deleteItem} updateQty={updateQty}/>
  );

  return(
    <table className="table table-borderless">
      <thead>
        <tr>
          <th></th>
          <th scope="col">Ingredients</th>
          <th scope="col">Qty</th>
          <th scope="col">Date</th>
          <th scope="col">EXP Date</th>
        </tr>
      </thead>
      <tbody>
        {row}
        <NewRow addItem={addItem}/>
      </tbody>
    </table>
  )
}

const FridgeItem = ({item, deleteItem, updateQty}) => {
  return(
    <tr>
      <td> 
        <button className="btn btn-round" onClick={() => deleteItem(item.id)}>
          <i className="bi bi-x-lg"></i>
        </button> 
      </td>
      <td> {item.ingr} </td>
      <td > <QtySelect qty={item.qty} id={item.id} updateQty={updateQty}/> </td>
      <td> {item.date.substring(0,10)} </td>
      <td> {item.exp_date.substring(0,10)} </td>
    </tr>
  )
}

const QtySelect = ({qty, id, updateQty}) => {
  const handleChange = (e) => {
    const new_qty = parseInt(e.target.value);
    updateQty(id, new_qty);
  }
  const options = [];
  for(var i = 1; i <= 15; i++) {
    options.push({value: i, label: i});
  }
  return (
    <select value={qty} onChange={handleChange}>
      {options.map(({value, label}, idx) => (<option key={idx} value={value}> {label} </option>))}
    </select>
  )
};

const NewRow= ({addItem}) => {
  const [add, setAdd] = useState(false);
  const [ingr, setIngr] = useState('');
  const [qty, setQty] = useState(1);
  const [date, setDate] = useState(new Date());
  const [expDate, setExpDate] = useState(new Date());
  const [ingrs, setIngrs] = useState([]);

  const cancel = () => {
    setAdd(false);
    setIngr('');
    setQty(1);
    setDate(new Date());
    setExpDate(new Date());
  }

  const save = () => {
    const offset = date.getTimezoneOffset();
        const newItem = {
      ingr: parseInt(ingr),
      qty: qty,
      date: new Date(date.getTime() - (offset*60*1000)).toISOString().substring(0,10),
      exp_date: new Date(expDate.getTime() - (offset*60*1000)).toISOString().substring(0,10)
    }
    addItem(newItem);
    cancel();
  }

  const handleSelect = (e) => {
    setIngr(e.target.value);
    var exp = new Date(date);
    const ingr = ingrs.find((element) => element.id == parseInt(e.target.value))
    exp.setDate(exp.getDate() + ingr.exp);
    setExpDate(exp);
  }

  useEffect(() => {
    IngrService.getAll()
    .then(({data}) => {
      setIngrs(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  if(add){
    return(
      <tr>
        <td><button onClick={save}/> <button onClick={cancel}/>
        </td>
        <td>
          <select value={ingr} onChange={handleSelect}>
            <option disabled value=''></option>
            {ingrs.map((ingr, idx) => (<option key={idx} value={ingr.id}> {ingr.name} </option>))}
          </select>
        </td>
        <td>
          <QtySelect qty={qty} updateQty={(_, new_qty) => setQty(new_qty)}/>
        </td>
        <td>
          <DatePicker selected={date} onChange={(date) => setDate(date)} />
        </td>
        <td>
          <DatePicker selected={expDate} onChange={(date) => setExpDate(date)} />
        </td>
      </tr>
    )
  }
  else{
    return(
      <tr>
        <td> 
          <button className="btn btn-round" onClick={() => setAdd(true)}>
            <i className="bi bi-plus-lg"></i>
          </button> 
        </td> 
      </tr>
    )
  } 
}

export default MyListComponent;
