import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import RecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipePage = () => {
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState(null);
  const [ingrs, setIngrs] = useState([]);
  const {recipeId} = useParams();

  useEffect(() => {
    RecipeService.getMyRecipe(recipeId)
    .then(({data}) => {
      setRecipe(data[0])
    }).catch((err) => {
      console.log(err)
    });

    RecipeService.getRecipeItem(recipeId)
    .then(({data}) => {
      console.log("useEffect")
      setIngrs(data);
    }).catch((err) => {
      console.log(err)
    });
  }, []);

  if(recipe){
    return (
      <main>
        <div className="container py-4">
          <div className="container-fluid py-5">
            <h2> 
              {recipe.name}
              <button className="btn btn-round" onClick={() => navigate(`/myrecipe/${recipe.id}/edit`)}>
                <i className="bi bi-pencil-fill"></i>
              </button> 
            </h2>
            <p> {recipe.desc} </p>
            <p>
              {ingrs.map((ingr, idx) => ingr.name)}
            </p>
            <p> {recipe.step} </p>
          </div>
        </div>
      </main>
    );
  }
  else{
    return(
      <></>
    )
  }

};

export default MyRecipePage;
