import React, { useState, useEffect, useParams } from "react";
import ListService from "../services/list.service";
import IngrService from "../services/ingr.service";
import "./style.css";

const MyListComponent = () => {
  const {id} = useParams();
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <MyList id={id}/>
        </div>
      </div>
    </main>
  );
};



const MyList = ({id}) => {
  return(
    <>
      <ListForm id={id}/>
      {/* <ListTable id={id}/> */}
    </>
  )
}

const ListForm = ({id}) => {
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [edit, setEdit] = useState(false);
  let listName, listDesc;

  const save = () => {
    ListService.updateList(name, desc);
    listName = name;
    listDesc = desc;
    setEdit(false);
  }
  const cancel = () => {

  }

  useEffect(() => {
    ListService.getList(id)
    .then(({data}) => {
      setName(data.name);
      setDesc(data.desc);
      listName = name;
      listDesc = desc;
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  if(edit){
    return(
      <div>
        <button className="btn btn-round" onClick={save}>
          <i className="bi bi-check-lg"></i>
        </button>
        <button className="btn btn-round" onClick={() => {setEdit(true)}}>
          <i className="bi bi-x-lg"></i>
        </button>
      </div>
    )
  }
  else{
    return(
      <div>
        <h3> {name} </h3>
        <button className="btn btn-round" onClick={() => {setEdit(true)}}>
          <i className="bi bi-pencil-fill"></i>
        </button>
        <p> {desc} </p>
      </div>
    )
  }
}

