import axios from "axios";
const API_URL = "http://localhost:8080/api/list";

class ListService {

  static getAll(){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.get(API_URL, {headers: {Authorization: token}});
  }

  static addList(name){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.post(API_URL, {name}, {headers: {Authorization: token}});
  }

  static updateList(id, name, desc){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.put(API_URL, {id, name, desc}, {headers: {Authorization: token}});
  }

  static updateQty(id, qty){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios.put(API_URL+"/qty", {id, qty}, {headers: {Authorization: token}});
  }

  static deleteList(id){
    let token;
    if (localStorage.getItem("user")) {
      token = JSON.parse(localStorage.getItem("user")).token;
    } else {
      token = "";
    }
    return axios({url: API_URL, method: 'delete', data : {id}, headers:{Authorization: token}});
  }
}

export default ListService;
