import React, { useState, useEffect } from "react";
import FridgeService from "../services/fridge.service";
import ListService from "../services/list.service";
import "./style.css";

const HomeComponent = () => {
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <Fridge/>
        </div>
      </div>
    </main>
  );
};



const Fridge = () => {
  return(
    <div className="row">
       <h3>Fridge</h3>
       <FridgeTable/>
     </div>
  )
}

const FridgeTable = () => {
  const [fridge, setFridge] = useState([]);

  const addFridge = (item) => {
    const new_fridge = [...fridge, item];
    setFridge(new_fridge);
  }

  const updateQty = (id, new_qty) => {
    const new_fridge = fridge.map(item => {
      if(item.id == id){
        item.qty = new_qty;
      } 
      return item;
    })
    setFridge(new_fridge);
    FridgeService.updateFridge(id, new_qty);
  }

  const deleteFridge = (id) => {
    setFridge(fridge.filter(a => a.id !== id));
    FridgeService.deleteFridge(id);
  }

  useEffect(() => {
    FridgeService.getFridge()
    .then(({data}) => {
      setFridge(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = fridge.map((item, idx) => 
    <FridgeItem key={idx} item={item} deleteFridge={deleteFridge} updateQty={updateQty}/>
  );

  return(
    <table className="table table-borderless">
      <thead>
        <tr>
          <th></th>
          <th scope="col">ingredient</th>
          <th scope="col">qty</th>
          <th scope="col">date</th>
          <th scope="col">exp date</th>
        </tr>
      </thead>
      <tbody>
        {row}
        <NewRow/>
      </tbody>
    </table>
  )
}

const FridgeItem = ({item, deleteFridge, updateQty}) => {
  return(
    <tr>
      <td> 
        <button className="btn btn-round" onClick={() => deleteFridge(item.id)}>
          <i className="bi bi-x-lg"></i>
        </button> 
      </td>
      <td> {item.ingr} </td>
      <td > <QtySelect qty={item.qty} id={item.id} updateQty={updateQty}/> </td>
      <td> {item.date.substring(0,10)} </td>
      <td> {item.exp_date.substring(0,10)} </td>
    </tr>
  )
}

const QtySelect = ({qty, id, updateQty}) => {
  const handleChange = (e) => {
    const new_qty = parseInt(e.target.value);
    updateQty(id, new_qty);
  }
  const options = [];
  for(var i = 1; i <= 10; i++) {
    options.push({'value': i, label: i});
  }
  return (
    <select value={qty} onChange={handleChange}>
      {options.map(({value, label}, idx) => (<option key={idx} value={value}> {label} </option>))}
    </select>
  )
};

const NewRow= () => {
  const [add, setAdd] = useState(false);
  const [ingr, setIngr] = useState('');
  const [qty, setQty] = useState(1);
  const [date, setDate] = useState('');
  const [expDate, setExpDate] = useState('');

  const save = () => {
    const newItem = {
      ingr: parseInt(ingr),
      qty: qty,
      date: date,
      exp_date: expDate
    }

  }

  if(add){
    return(
      <tr>
        <td><button /> <button onClick={() => setAdd(false)}/></td>
        <td>
          <input value={ingr} onChange={e => setIngr(e.target.value)}/>
        </td>
        <td>
          <QtySelect qty={qty} updateQty={(id, new_qty) => setQty(new_qty)}/>
        </td>
        <td>
          <input value={date} onChange={e => setDate(e.target.value)}/>
        </td>
        <td>
          <input value={expDate} onChange={e => setExpDate(e.target.value)}/>
        </td>
      </tr>
    )
  }
  else{
    return(
      <tr>
        <td> 
          <button className="btn btn-round" onClick={() => setAdd(true)}>
            <i className="bi bi-plus-lg"></i>
          </button> 
        </td> 
      </tr>
    )
  }

}

