const NewRow= ({addItem}) => {
  const [add, setAdd] = useState(false);
  const [ingr, setIngr] = useState('');
  const [qty, setQty] = useState(1);
  const [date, setDate] = useState(new Date());
  const [expDate, setExpDate] = useState(new Date());
  const [ingrs, setIngrs] = useState([]);

  const cancel = () => {
    setAdd(false);
    setIngr('');
    setQty(1);
    setDate(new Date());
    setExpDate(new Date());
  }

  const save = () => {
    const offset = date.getTimezoneOffset();
        const newItem = {
      ingr: parseInt(ingr),
      qty: qty,
      date: new Date(date.getTime() - (offset*60*1000)).toISOString().substring(0,10),
      exp_date: new Date(expDate.getTime() - (offset*60*1000)).toISOString().substring(0,10)
    }
    addItem(newItem);
    cancel();
  }

  const handleSelect = (e) => {
    setIngr(e.target.value);
    var exp = new Date(date);
    const ingr = ingrs.find((element) => element.id == parseInt(e.target.value))
    exp.setDate(exp.getDate() + ingr.exp);
    setExpDate(exp);
  }

  useEffect(() => {
    IngrService.getAll()
    .then(({data}) => {
      setIngrs(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  if(add){
    return(
      <tr>
        <td><button onClick={save}/> <button onClick={cancel}/>
        </td>
        <td>
          <select value={ingr} onChange={handleSelect}>
            <option disabled value=''></option>
            {ingrs.map((ingr, idx) => (<option key={idx} value={ingr.id}> {ingr.name} </option>))}
          </select>
          {/* <input value={ingr} onChange={e => setIngr(e.target.value)}/> */}
        </td>
        <td>
          <QtySelect qty={qty} updateQty={(_, new_qty) => setQty(new_qty)}/>
        </td>
        <td>
          <DatePicker selected={date} onChange={(date) => setDate(date)} />
        </td>
        <td>
          <DatePicker selected={expDate} onChange={(date) => setExpDate(date)} />
        </td>
      </tr>
    )
  }
  else{
    return(
      <tr>
        <td> 
          <button className="btn btn-round" onClick={() => setAdd(true)}>
            <i className="bi bi-plus-lg"></i>
          </button> 
        </td> 
      </tr>
    )
  } 
}

export default AllListComponent;
