import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import ListService from "../../services/list.service";
import { Container } from "react-bootstrap";
import "../style.css";

const AllList = () => {
  return (
    <main>
      <Container className="py-5">
        <Lists/>
      </Container>
    </main>
  );
};


const Lists = () => {
  const [lists, setLists] = useState([]);
  const navigate = useNavigate();

  const addList = () => {
    const name = `New List ${lists.length+1}`;
    ListService.addList(name, 'This is a new shopping list.')
    .then(({data}) => {
      navigate(`/mylist/${data.insertId}`);
    })
    .catch((err) => {
      console.log(err);
    });
  }

  const deleteList = (id) => {
    setLists(lists.filter(list => list.id !== id));
    ListService.deleteList(id);
  }

  useEffect(() => {
    ListService.getAll()
    .then(({data}) => {
      setLists(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = lists.map((list, idx) => 
    <List key={idx} list={list} deleteList={deleteList}/>
  );

  return(
    <div className="row">
      <h3>
        Shopping Lists
        <button className="btn btn-round" onClick={addList}>
          <i className="bi bi-plus-lg"></i>
        </button> 
      </h3>
      {row}
    </div>
  )
}

const List = ({list, deleteList}) => {
  const navigate = useNavigate();
  return(
    <h4>
      <button className="btn btn-round" onClick={() => {deleteList(list.id)}}>
        <i className="bi bi-x-lg"></i>
      </button>
      {list.name}
      <button className="btn btn-round" onClick={() => {navigate(`/mylist/${list.id}`)}}>
        <i className="bi bi-pencil-fill"></i>
      </button> 
    </h4>
  )
}

export default AllList;
