import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row } from "react-bootstrap";
import MyRecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipeHome = () => {
  return (
    <main>
      <Container fluid>
          <MyRecipe/>
          <LikedRecipe/>
      </Container>
    </main>
  );
};

const Recipe = ({recipe}) => {
  const navigate = useNavigate()
  return(
    <div>
      <h5> 
        <a href={`/myrecipe/${recipe.id}`}>{recipe.name}</a>
        <button className="btn btn-round" onClick={() => navigate(`/myrecipe/${recipe.id}/edit`)}>
          <i className="bi bi-pencil-fill"></i>
        </button> 
      </h5>
    </div>
  );
};

const MyRecipe = () => {
  const [recipes, setRecipes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    MyRecipeService.getAllRecipe()
    .then(({data}) => {
      setRecipes(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return(
    <Container className="py-5">
      <Row lg>
        My Recipes
          <button className="btn btn-round" onClick={() => navigate("/myrecipe/newrecipe")}>
            <i className="bi bi-plus-lg"></i>
          </button> 
      </Row>
      {recipes.map((recipe, idx) => 
        <Recipe key={idx} recipe={recipe}/>
      )}
    </Container>
  );
};

const LikedRecipe = () => {
  return(
    <div>
      <h3> Liked Recipes </h3>
    </div>
  );
};

export default MyRecipeHome;
