import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Card, Form, Button } from "react-bootstrap";
import RecipeService from "../../services/recipe.service";
import IngrService from "../../services/ingr.service";
import Select from 'react-select';
import "../style.css";

const ExploreRecipe = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    RecipeService.getAllRecipe()
    .then(({data}) => {
      setRecipes(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return(
    <main>
      <Container className="py-5">
        <Row> 
          <h2> EXPLORE RECIPES </h2>
          <hr/>
        </Row>
        <SearchBar setRecipes={setRecipes}/>
        <Row xs={1} md={3} className="g-4">
          {recipes.slice(0, 51).map((recipe, idx) => (
            <Recipe key={idx} recipe={recipe}/>
          ))}
        </Row>
      </Container>
    </main>
  )
};

const SearchBar = ({setRecipes}) => {
  const [newIngrs, setNewIngrs] = useState([]);
  const [allIngrs, setAllIngrs] = useState([]);

  useEffect(() => {
    IngrService.getAll()
    .then(({data}) => {
      const temp = data.map((element) => {return {value: element.id, label: element.name}});
      setAllIngrs(temp);
    }).catch((err) => {
      console.log(err)
    });
  }, []);

  return(
    <>
    <Row>
    <Form>
      <Form.Check
        inline
        label="Most Liked"
        name="recipe"
        type="radio"
        id="1"
      />
      <Form.Check
        inline
        label="Search By Ingredients"
        name="group1"
        type="radio"
        id="2"
      />
      <Form.Check
        inline
        label="Search By Tags"
        name="group1"
        type="radio"
        id="3"
      />
    </Form>
    </Row>
    <Row>
      <Col fluid>
      <Select
        name="ingrs"
        className="basic-multi-select"
        classNamePrefix="select"
        options={allIngrs}
        value={newIngrs}
        isMulti
        closeMenuOnSelect={false}
        onChange={(value) => setNewIngrs(value)}
      />
      </Col>
      <Col><button/></Col>
    </Row>
    </>
  )
}

const Recipe = ({recipe}) => {
  const navigate = useNavigate()
  return(
    <Col>
      <Button className="p-1" variant="outline-secondary" 
       onClick={() => navigate(`/recipes/${recipe.id}`)}>
        <Card>
          <Card.Img variant="top" src="boba.jpg"/>
          <Card.Body>
            <Card.Title as="h4"> {recipe.name.toUpperCase()} </Card.Title>
          </Card.Body>
        </Card>
      </Button>
    </Col>
  );
};



export default ExploreRecipe;
