import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import RecipeService from "../../services/myrecipe.service";
import IngrService from "../../services/ingr.service";
import Select from 'react-select';
import {Modal, Form, Button} from 'react-bootstrap';
import "../style.css";

const EditRecipePage = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [ingrs, setIngrs] = useState([]);
  const [steps, setSteps] = useState('');
  const [allIngrs, setAllIngrs] = useState([]);
  const {recipeId} = useParams();

  const handleChange = (value) => {
    setIngrs(value);
    console.log(ingrs);
  };

  useEffect(() => {
    RecipeService.getMyRecipe(recipeId)
    .then(({data}) => {
      setName(data[0].name);
      setDesc(data[0].desc);
      setSteps(data[0].step);
    }).catch((err) => {
      console.log(err)
    });

    RecipeService.getRecipeItem(recipeId)
    .then(({data}) => {
      const temp = 
      setIngrs(data);
    }).catch((err) => {
      console.log(err)
    });

    IngrService.getAll()
    .then(({data}) => {
      const temp = data.map((element) => {return {value: element.id, label: element.name}});
      setAllIngrs(temp);
    }).catch((err) => {
      console.log(err)
    });
  }, []);

  return (
    <div>
      <Modal show={true} centered scrollable size='xl' 
        backdrop="static" onHide={() => navigate(`/myrecipe/${recipeId}`)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Recipe</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="name">
              <Form.Label> Name </Form.Label>
              <Form.Control type="name" defaultValue={name}/>
            </Form.Group>
            <Form.Group className="mb-3" controlId="desc">
              <Form.Label> Desciption </Form.Label>
              <Form.Control type="desc" defaultValue={desc}/>
            </Form.Group>
            <Form.Group className="mb-3" controlId="ingrs">
              <Form.Label> Ingredients </Form.Label>
              <Select
                name="ingrs"
                className="basic-multi-select"
                classNamePrefix="select"
                options={allIngrs}
                value={ingrs}
                isMulti
                closeMenuOnSelect={false}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="steps">
              <Form.Label> Steps </Form.Label>
              <Form.Control as="textarea" rows={10} defaultValue={steps}/>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => navigate(`/myrecipe/${recipeId}`)}>
            Cancel
          </Button>
          <Button variant="danger" onClick={() => {}}>
            Delete
          </Button>
          <Button variant="primary" onClick={() => {}}>
            Save
          </Button>
        </Modal.Footer>
      </Modal> 
    </div>
  );
};

export default EditRecipePage;
