import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import ListService from "../services/list.service";
import IngrService from "../services/ingr.service";
import "./style.css";

const MyListComponent = () => {
  const {listId} = useParams();
  return (
    <div className="container py-4">
      <div className="container-fluid py-5">
        <MyList listId={listIdid}/>
      </div>
    </div>
  );
};



const MyList = ({listId}) => {
  return(
    <>
      <ListForm listId={listId}/>
      <ListTable listId={listId}/>
    </>
  )
}

const ListForm = ({listId}) => {
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [edit, setEdit] = useState(false);

  const save = () => {
    setEdit(false);
    ListService.updateList(listId, name, desc);
  }

  useEffect(() => {
    ListService.getList(listId)
    .then(({data}) => {
      setName(data[0].name);
      setDesc(data[0].desc);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  if(edit){
    return(
      <div>
        <button className="btn btn-round" onClick={save}>
          <i className="bi bi-check-lg"></i>
        </button>
        <label>
          Name:
          <input value={name} onChange={e => setName(e.target.value)} />
        </label>
        <label>
          Desc:
          <textarea value={desc} onChange={e => setDesc(e.target.value)} />
        </label>
      </div>
    )
  }
  else{
    return(
      <div>
        <h3> 
          {name} 
          <button className="btn btn-round" onClick={() => {setEdit(true)}}>
            <i className="bi bi-pencil-fill"></i>
          </button>
        </h3>
        <p> {desc} </p>
      </div>
    )
  }
}

const ListTable = ({listId}) => {
  const [items, setItems] = useState([]);

  const addItem = (item) => {
    const new_items = [...fridge, item];
    setItems(new_items);
    console.log("add");
    // FridgeService.addFridge(item);
  }

  const updateQty = (itemId, new_qty) => {
    const new_items = items.map(item => {
      if(item.id == itemId){
        item.qty = new_qty;
      } 
      return item;
    })
    setItems(new_items);
    console.log("update");
    // FridgeService.updateFridge(id, new_qty);
  }

  const deleteItem = (itemId) => {
    console.log("delete");
    setFridge(items.filter(item => item.id !== itemId));
    // FridgeService.deleteFridge(id);
  }

  useEffect(() => {
    ListService.getItem(listId)
    .then(({data}) => {
      setItems(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = items.map((item, idx) => 
    <ListItem key={idx} item={item} deleteItem={deleteItem} updateQty={updateQty}/>
  );

  return(
    <table className="table table-borderless">
      <thead>
        <tr>
          <th></th>
          <th scope="col">Ingredients</th>
          <th scope="col">Qty</th>
        </tr>
      </thead>
      <tbody>
        {row}
        <NewRow addItem={addItem}/>
      </tbody>
    </table>
  )
}

const ListItem = ({item, deleteItem, updateQty}) => {
  return(
    <tr>
      <td> 
        <button className="btn btn-round" onClick={() => deleteItem(item.id)}>
          <i className="bi bi-x-lg"></i>
        </button> 
      </td>
      <td> {item.ingr} </td>
      <td > <QtySelect qty={item.qty} itemId={item.id} updateQty={updateQty}/> </td>
    </tr>
  )
}

const QtySelect = ({qty, itemId, updateQty}) => {
  const handleChange = (e) => {
    const new_qty = parseInt(e.target.value);
    updateQty(itemId, new_qty);
  }
  const options = [];
  for(var i = 1; i <= 15; i++) {
    options.push({value: i, label: i});
  }
  return (
    <select value={qty} onChange={handleChange}>
      {options.map(({value, label}, idx) => (<option key={idx} value={value}> {label} </option>))}
    </select>
  )
};

const NewRow= ({addItem}) => {
  const [add, setAdd] = useState(false);
  const [ingr, setIngr] = useState('');
  const [qty, setQty] = useState(1);
  const [ingrs, setIngrs] = useState([]);

  const cancel = () => {
    setAdd(false);
    setIngr('');
    setQty(1);
  }

  const save = () => {
    const newItem = {
      ingr: parseInt(ingr),
      qty: qty,
    }
    addItem(newItem);
    cancel();
  }

  const handleSelect = (e) => {
    setIngr(e.target.value);
  }

  useEffect(() => {
    IngrService.getAll()
    .then(({data}) => {
      setIngrs(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  if(add){
    return(
      <tr>
        <td>
          <button className="btn btn-round" onClick={save}>
            <i class="bi bi-check-lg"></i>
          </button>
          <button className="btn btn-round" onClick={cancel}>
            <i className="bi bi-x-lg"></i>
          </button>
        </td>
        <td>
          <select value={ingr} onChange={handleSelect}>
            <option disabled value=''></option>
            {ingrs.map((ingr, idx) => (<option key={idx} value={ingr.id}> {ingr.name} </option>))}
          </select>
        </td>
        <td>
          <QtySelect qty={qty} updateQty={(_, new_qty) => setQty(new_qty)}/>
        </td>
      </tr>
    )
  }
  else{
    return(
      <tr>
        <td> 
          <button className="btn btn-round" onClick={() => setAdd(true)}>
            <i className="bi bi-plus-lg"></i>
          </button> 
        </td> 
      </tr>
    )
  } 
}

export default MyListComponent;
