import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import RecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipePage = () => {
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [ingrs, setIngrs] = useState([]);
  const [steps, setSteps] = useState('');
  const {recipeId} = useParams();

  useEffect(() => {
    RecipeService.getMyRecipe(recipeId)
    .then(({data}) => {
      console.log(data);
      setName(data[0].name);
      setDesc(data[0].desc);
      setSteps(data[0].step);
    }).catch((err) => {
      console.log(err)
    });

    RecipeService.getRecipeItem(recipeId)
    .then(({data}) => {
      console.log(data);
      setIngrs(data);
    }).catch((err) => {
      console.log(err)
    });
  }, []);

  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <h2> 
            {name} 
          </h2>
          <p> {desc} </p>
          <ul>
            {ingrs.map((ingr, idx) => <li key={idx}> {ingr.name} </li>)}
          </ul>
          <p> {steps} </p>
        </div>
      </div>
    </main>
  );
};

export default MyRecipePage;
