import React, { useState, useEffect } from "react";
import ListService from "../services/list.service";
import "./style.css";

const AllListComponent = () => {
  return (
    <main>
      <div className="container py-4">
        <div className="container-fluid py-5">
          <AllList/>
        </div>
      </div>
    </main>
  );
};


const AllList = () => {
  const [lists, setLists] = useState([]);

  // const addItem = (item) => {
  //   const new_fridge = [...fridge, item];
  //   setFridge(new_fridge);
  //   FridgeService.addFridge(item);
  // }

  // const deleteItem = (id) => {
  //   setFridge(fridge.filter(item => item.id !== id));
  //   FridgeService.deleteFridge(id);
  // }

  useEffect(() => {
    ListService.getAll()
    .then(({data}) => {
      setLists(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  const row = lists.map((item, idx) => 
    <List key={idx} item={item} deleteItem={deleteItem} updateQty={updateQty}/>
  );

  return(
    <div className="row">
      <h3>Shopping Lists</h3>
      <button>new</button>
      <table className="table table-borderless">
        <tbody>
          {row}
          <NewRow addItem={addItem}/>
        </tbody>
      </table>
    </div>
  )
}

const List = ({item, deleteItem, updateQty}) => {
  return(
    <tr>
      <td> 
        <button className="btn btn-round" onClick={() => deleteItem(item.id)}>
          <i className="bi bi-x-lg"></i>
        </button> 
      </td>
      <td> {item.ingr} </td>
      <td > <QtySelect qty={item.qty} id={item.id} updateQty={updateQty}/> </td>
      <td> {item.date.substring(0,10)} </td>
      <td> {item.exp_date.substring(0,10)} </td>
    </tr>
  )
}

