import React, { useState, useEffect } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import UserProvider from "./context/user-context";

import NavBar from "./components/navbar-component";
import BobaHome from "./components/boba-component";
import Register from "./components/register-component";
import Login from "./components/login-component";
import Private from "./components/private-component";
import Home from "./components/home-component";
import AllList from "./components/allList-component";
import MyList from "./components/mylist-component";
import './App.css';
import 'bootstrap-icons/font/bootstrap-icons.css'

function App() {
  // const [loginState, setLoginState] = useState(UserService.isLogin());

  // useEffect(() => {
  //   if(loginState)
  //     console.log("Login");
  //   else
  //     console.log("not login");
  // }, [loginState])

  return (
    <div className="App">
      <UserProvider>
        <NavBar/>
        <Routes>
          <Route path="/" exact element={<BobaHome/>} />
          <Route path="/login" element={<Login/>} />
          <Route path="/register" element={<Register/>} />
          <Route path="/home" element={<Private component={Home}/>} /> 
          <Route path="/mylist" element={<Private component={AllList}/>} />
          <Route path="/mylist/:listId" element={<Private component={MyList}/>} />
        </Routes>
      </UserProvider>
    </div>
  );
}

export default App;
