import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Card, Button, Nav } from "react-bootstrap";
import MyRecipeService from "../../services/myrecipe.service";
import "../style.css";

const MyRecipeHome = () => {
  const [liked, setLiked] = useState(false);

  return(
    <main>
      <Container className="py-5">
        <NavBar setLiked={setLiked}/>
        {!show && (<MyRecipe/>)}
        {show && (<MyRecipe/>)}
      </Container>
    </main>
  )
};
// (e) => setShow(e.target.dataset.rrUiEventKey)
const NavBar = ({setLiked}) => {
  return(
    <Row>
      <Nav fill variant="tabs" defaultActiveKey="1">
        <Nav.Item>
          <Nav.Link eventKey="1" onClick={() => setLiked(false)}> 
            <h2>MY RECIPES</h2>
          </Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link eventKey="2" onClick={() => setLiked(true)}>
            <h2>LIKED RECIPES</h2>
          </Nav.Link>
        </Nav.Item>
      </Nav>
    </Row>
  )
};

const Recipe = ({recipe}) => {
  const navigate = useNavigate()
  return(
    <Col>
      <Button style={{ marginLeft: "auto" }} className="p-1" variant="outline-secondary" 
       onClick={() => navigate(`/myrecipe/${recipe.id}`)}>
        <Card>
          <Card.Img variant="top" src="boba.jpeg" />
          <Card.Body>
            <Card.Title as="h4"> {recipe.name} </Card.Title>
            <Card.Text style={{textAlign:"left"}}> {recipe.desc} </Card.Text>
          </Card.Body>
        </Card>
      </Button>
    </Col>
  );
};

const MyRecipe = () => {
  const [recipes, setRecipes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    MyRecipeService.getAllRecipe()
    .then(({data}) => {
      setRecipes(data);
    })
    .catch((err) => {
      console.log(err)
    });
  }, [])

  return(
    <Container className="py-4">
      {/* <Row>
        <h2>
          MY RECIPES
          <button className="btn btn-round" onClick={() => navigate("/myrecipe/newrecipe")}>
            <i className="bi bi-plus-lg"></i>
          </button>
        </h2>
        <hr/>
      </Row> */}
      <Row xs={1} md={3} className="g-4">
        {recipes.map((recipe, idx) => (
          <Recipe key={idx} recipe={recipe}/>
        ))}
      </Row>
    </Container>
  );
};

const LikedRecipe = () => {
  return(
    <div>
      <h3> Liked Recipes </h3>
    </div>
  );
};

export default MyRecipeHome;
